import React, { useCallback } from 'react'
import { useContext, useEffect, useState } from 'react'
import { AppContext } from './Context'
import { useParams } from 'react-router-dom'
import { Link } from 'react-router-dom'



const SingleProduct = () => {

    const [loading, setLoading] = useState(false)
    const [product, setProduct] = useState({})

    
    const { id } = useParams()
    console.log(id)

    const data = useContext(AppContext)

    const url = `https://fakestoreapi.com/products/${id}`
    console.log(url)

    // const getData = useCallback(() => {
    //     setLoading(true)
    //     const fetchData = async () => {
    //         try {
    //             const data = await fetch(url)
    //             const response = await data.json()
    //             console.log(response)
    //             setProduct(response)

    //         } catch (error) {
    //             console.error("Error fetching data:", error);
    //         }

    //     }

    //     fetchData()

    // },[url])


    // useEffect(() => {
    //     getData()

    // }, [getData])

    useCallback(()=>{},[])

    const fetchData = async () => {
        try {
            const data = await fetch(url)
            const response = await data.json()
            console.log(response)
            setProduct(response)
            setLoading(false)

        } catch (error) {
            console.error("Error fetching data:", error);
        }

    }


    useEffect(()=>{
        setLoading(true)
            // const fetchData = async () => {
            //     try {
            //         const data = await fetch(url)
            //         const response = await data.json()
            //         console.log(response)
            //         setProduct(response)
            //         setLoading(false)
    
            //     } catch (error) {
            //         console.error("Error fetching data:", error);
            //     }
    
            // }
    
            fetchData()
            console.log(product)
    
        },[])



    // console.log(data[`${id - 1}`].title)

    if(loading){
        return (
            <div>LOADING....</div>
        )
    }

    console.log(product)

    const {image, title, price, description} = product


    return (
        <div className='container row mt-5 mx-auto text-center d-flex justify-content-center align-items-center'>
            <div className='col-lg col-sm-12 text-center mt-5'>
                <img src={image} style={{ height: '430px' }} className='mt-5'></img>
            </div>
            <div className='col-lg col-sm-12 '>
                <h1>{title}</h1>
                <p className=''>{description}</p>
                <h3>${price}</h3>
                <Link to={'/products'}>
                    <button className='btn btn-light btn-lg mt-5'>Back to Products List</button>
                </Link>
            </div>


        </div>
    )
}

export default SingleProduct


