import React from 'react'
import { useContext } from 'react'
import { AppContext } from './Context'
import ProductCard from './ProductCard'
import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';


const Products = () => {

    const data = useContext(AppContext)
    console.log(data)

    return (
        <div>
            <h1 className='text-center mt-5'>My Products</h1>

            <div className='container mt-5'>
                <div className='abg'>
                    {data.map(item => {
                        return (
                            <Card key={item.id} style={{ width: '18rem', height: '600px', padding:'30px' }}>
                                <Card.Img variant="top" src={item.image} style={{height: '200px'}} />
                                <Card.Body>
                                    <Card.Title style={{height:'50px', overflow:'auto'}}>{item.title}</Card.Title>
                                    <h3>${item.price.toFixed(2)}</h3>
                                    <Button variant="primary"  style={{ verticalAlign:'bottom'}}>Go somewhere</Button>
                                </Card.Body>
                            </Card>
                        )
                    })}
                </div>

            </div>




        </div>
    )
}

export default Products
