import Container from 'react-bootstrap/Container';
import Nav from 'react-bootstrap/Nav';
import Navbar from 'react-bootstrap/Navbar';
import NavDropdown from 'react-bootstrap/NavDropdown';
import 'bootstrap/dist/css/bootstrap.min.css';
import SunLogo from './sun-logo.png'
import { NavLink } from 'react-router-dom';
import './App.css'
import { useState } from 'react';


function NavbarMain() {

  const [activeKey, setActiveKey] = useState(window.location.pathname);

  const handleActiveNavLink = (event) => {
    setActiveKey(event.target.href);


    return (
      <Navbar expand="lg" className="bg-dark" variant='dark'>
        <Container>
          <Navbar.Brand href="/">
            <img src={SunLogo} alt="" className='nav-link logo-navbar' />
          </Navbar.Brand>
          <Navbar.Toggle aria-controls="basic-navbar-nav" />
          <Navbar.Collapse id="basic-navbar-nav">
            <Nav className="ms-auto" activeKey={activeKey}>
              <NavLink to="/" className='nav-link fs-4 ``' onClick={handleActiveNavLink}>Home</NavLink>
              <NavLink to="/products" className='nav-link fs-4' onClick={handleActiveNavLink}>Products</NavLink>
              <NavLink to="/contacts" className='nav-link fs-4' onClick={handleActiveNavLink}>Contact</NavLink>
              <NavLink to="/about" className='nav-link fs-4' onClick={handleActiveNavLink}>About Us</NavLink>
            </Nav>
          </Navbar.Collapse>
        </Container>
      </Navbar>
    );
  }}

  export default NavbarMain;

