import React, { useState, useEffect } from 'react';

const FeaturedProducts = () => {
    const [list, setList] = useState([]);
    const [featured, setFeatured] = useState([]);
    const [filteredIndices, setFilteredIndices] = useState([]);

    useEffect(() => {
        const fetchData = async () => {
            try {

                const data = await fetch('https://fakestoreapi.com/products');
                const response = await data.json();
                setList(response);

            } catch (error) {
                console.error("Error fetching data:", error);

            }

        };

        fetchData();

        let array = []
        for (let index = 0; index < 4; index++) {
            const randomNumber = Math.floor(Math.random() * 21);
            array.push(randomNumber);
        }

        setFeatured(array)

    }, []); // Empty dependency array ensures the effect runs once after the initial render

    useEffect(()=>{
        let filteredIndicesArray = list
            .map((item, index) => (featured.includes(item.id) ? item.id : null))
            .filter(item => item !== null);

        setFilteredIndices(filteredIndicesArray)
    },[list])


    console.log(list)
    console.log(featured)
    console.log(filteredIndices)





    // return (
    //     <div className='container mt-5'>
    //         <h1 className='text-center'>Our This Month's Featured Products</h1>
    //         <ul>
    //             {filteredIndices.map(index => (
    //                 <li key={index}>{list[index].title}</li>
    //             ))}
    //         </ul>
    //     </div>
    // );

    return (
        <div className='container mt-5'>
            {filteredIndices.length === 0 ? (
                <p>Loading...</p>
            ) : (
                <>
                    <h1 className='text-center'>Our This Month's Featured Products</h1>
                    <ul>
                        {filteredIndices.map(item => (
                            <li key={item.id}>{list[item.id].title}</li>
                        ))}
                    </ul>
                </>
            )}
        </div>
    );

};

export default FeaturedProducts;
