import Container from 'react-bootstrap/Container';
import Nav from 'react-bootstrap/Nav';
import Navbar from 'react-bootstrap/Navbar';
import NavDropdown from 'react-bootstrap/NavDropdown';
import 'bootstrap/dist/css/bootstrap.min.css';
import SunLogo from './sun-logo.png'
import { NavLink } from 'react-router-dom';
import './App.css'
import logo from './107.jpg'
import { useState, useEffect } from 'react';


function NavbarMain() {

  const [scroll, setScroll] = useState(false)

  const handleScroll = () => {
    if (window.scrollY > 20) {
      setScroll(true)
    } else {
      setScroll(false)
    }
  }

  useEffect(() => {
    window.addEventListener('scroll', handleScroll);
    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  })

  return (

    
    <Navbar expand="lg" className="bg-dark" fixed='top' variant={scroll? "":""}>
      <Container>
        <NavLink to="/">
          <img src={logo} alt="" className='nav-link logo-navbar abc' />
        </NavLink>
        <Navbar.Toggle aria-controls="basic-navbar-nav" />
        <Navbar.Collapse id="basic-navbar-nav">
          <Nav className="ms-auto">
            <NavLink to="/" className='nav-link fs-4'>Home</NavLink>
            <NavLink to="/products" className='nav-link fs-4'>Products</NavLink>
            <NavLink to="/contacts" className='nav-link fs-4'>Contact</NavLink>
            <NavLink to="/about" className='nav-link fs-4'>About Us</NavLink>
          </Nav>
        </Navbar.Collapse>
        {/* {scroll? <h1 style={{color:'white'}}>TRUEE</h1>:'false'} */}
      </Container>
    </Navbar>
  );
}

export default NavbarMain;

